class IncomeStatement < ActiveRecord::Base
has_one :personal_incomestatement
	def self.totalllll
find_by_sql("select income_statements.id,income_statements.date,income_statements.ses_business_revenue, personal_incomestatements.partner + personal_incomestatements.car_loan + personal_incomestatements.lower + personal_incomestatements.credit + personal_incomestatements.shopping_loan + personal_incomestatements.yokoi_info + personal_incomestatements.friend + personal_incomestatements.family + personal_incomestatements.health_care + personal_incomestatements.basic_costs + personal_incomestatements.clothes_costs + personal_incomestatements.reisure_costs + personal_incomestatements.ironman_costs + personal_incomestatements.car_tax + personal_incomestatements.car_insurance + personal_incomestatements.social_insurance + personal_incomestatements.life_insurance + personal_incomestatements.housing_costs + personal_incomestatements.income_tax as representative_reward,income_statements.personnel_expenses,income_statements.sales_support_fee,income_statements.social_insurance_costs,income_statements.tax_expense,income_statements.business_tax,income_statements.delay_loss,income_statements.communications_expenses,income_statements.transportation_cost,income_statements.capital_investment,income_statements.advertising_expenses,income_statements.entertainment_expenses,income_statements.affairs_costs,income_statements.shareholder_costs,income_statements.lower_costs,income_statements.debt_costs,income_statements.realestate, income_statements.personnel_expenses +  income_statements.sales_support_fee +  income_statements.social_insurance_costs +  income_statements.tax_expense +  income_statements.business_tax +  income_statements.delay_loss +  income_statements.communications_expenses +  income_statements.transportation_cost +  income_statements.capital_investment +  income_statements.advertising_expenses +  income_statements.entertainment_expenses +  income_statements.affairs_costs +  income_statements.shareholder_costs +  income_statements.lower_costs +  income_statements.debt_costs +  income_statements.realestate as cost_sum from income_statements left outer join personal_incomestatements on income_statements.date = personal_incomestatements.date where income_statements.date >= '2016-01-01'")
	end

end
